package operators;

public class LsAndR {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 8;

        System.out.println("Original number: " + num);
        System.out.println("Left shift by 2: " + (num << 2));   
        System.out.println("Right shift by 2: " + (num >> 2));

	}

}
